INSERT INTO `dh_feedback` VALUES ('118', '烛龙OL', '怪物制作2套1230', 'khajiit', '杨帆,邓小欢,刘秀,申晓彤', '<p><strong>Khajiit:</strong></p><p>Z59_qt<span style=\"font-family:宋体\">：收招快点。第</span>40<span style=\"font-family:宋体\">到</span>50<span style=\"font-family:宋体\">帧的收招时间缩短</span>5<span style=\"font-family:宋体\">帧。</span></p><p><span style=\"color:red\">Z60_qt: </span><span style=\"font-family:宋体;color:red\">最后一拳力度太小，给人感觉虎头蛇尾。因为是猫科类，弹跳力比较突出，所以最后一拳可以蹦起来斜向下猛挥。</span></p><p>Z65_qt:<span style=\"font-family: 宋体\">冲刺阶段上半身应该处于预备姿态（收），在到达瞬间再发力（张），这样才会冲击感。当前是在冲刺过程中就处于半发力状态了，这样到达的时候冲击感会弱很多。</span></p><p>Z66_qt<span style=\"font-family:宋体\">：预备姿势可以强化点辨识感，让人一看姿势就知道要跳了，后面没什么问题。</span></p><p><span style=\"font-size:14px;font-family: &#39;Calibri&#39;,&#39;sans-serif&#39;\">Z77_qt:</span><span style=\"font-size:14px;font-family:宋体\">第</span><span style=\"font-size:14px;font-family:&#39;Calibri&#39;,&#39;sans-serif&#39;\">28</span><span style=\"font-size:14px;font-family:宋体\">帧的时候，双臂再往上抬点即可。</span></p><p><br/></p>', '朱惠杰', '2017-02-10 17:43:06', '朱惠杰', '2017-02-10 17:43:06', ',', '申晓彤,杨帆,刘秀,邓小欢');
INSERT INTO `dh_feedback` VALUES ('119', '烛龙OL', '怪物制作2套1230', 'khajiit', '杨帆,邓小欢,刘秀,申晓彤', '<p><strong>Khajiit:</strong></p><p>Z59_qt<span style=\"font-family:宋体\">：收招快点。第</span>40<span style=\"font-family:宋体\">到</span>50<span style=\"font-family:宋体\">帧的收招时间缩短</span>5<span style=\"font-family:宋体\">帧。</span></p><p><span style=\"color:red\">Z60_qt: </span><span style=\"font-family:宋体;color:red\">最后一拳力度太小，给人感觉虎头蛇尾。因为是猫科类，弹跳力比较突出，所以最后一拳可以蹦起来斜向下猛挥。</span></p><p>Z65_qt:<span style=\"font-family: 宋体\">冲刺阶段上半身应该处于预备姿态（收），在到达瞬间再发力（张），这样才会冲击感。当前是在冲刺过程中就处于半发力状态了，这样到达的时候冲击感会弱很多。</span></p><p>Z66_qt<span style=\"font-family:宋体\">：预备姿势可以强化点辨识感，让人一看姿势就知道要跳了，后面没什么问题。</span></p><p><span style=\"font-size:14px;font-family: &#39;Calibri&#39;,&#39;sans-serif&#39;\">Z77_qt:</span><span style=\"font-size:14px;font-family:宋体\">第</span><span style=\"font-size:14px;font-family:&#39;Calibri&#39;,&#39;sans-serif&#39;\">28</span><span style=\"font-size:14px;font-family:宋体\">帧的时候，双臂再往上抬点即可。</span></p><p><br/></p>', '朱惠杰', '2017-02-10 17:43:09', '朱惠杰', '2017-02-10 17:43:09', '邓小欢,杨帆', '申晓彤,杨帆,刘秀,邓小欢');
